import React, { useState } from "react";
import { useQuery, gql } from '@apollo/client';


const ProjectList = () => {
  const [projects, setProjects] = useState([]);

  return (
    <div>
      <h3>Project List</h3>
    </div>
  );
};

const GET_ALL_PROJECTS = gql`
  query getAllProjects {
    getAllProjects {
        id
        name
        users {
          id
          name
          email
        }
        tasks {
          id
          name
          assignee {
            id
            email
            name
          }
          assigneedBy {
            id
            name
            email
          }
        }
      }
  }
`;

export default ProjectList;
