import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { useQuery } from "@apollo/client";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";

import { GET_PROJECT_BY_ID } from "../../graphql/graphqlQuery";

const Project = () => {
  const location = useLocation();
  // console.log({ prerna: location.state.id });

  const [tasksArray , setTasksArray] = useState([]);

  const { loading, error, data } = useQuery(GET_PROJECT_BY_ID, {
    variables: { id: location.state.id },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>error</p>;

  console.log("data", data, "error", error);

  if(data){
    const tempTasks = data.getProjectById.tasks;
    console.log("----->TASKSSS",tempTasks);
  }

  return (
    <div className="project-body">
      {data && data.getProjectById && (
        <>
          <h3 className="text-left">{data.getProjectById.name}</h3>
          <Grid item xs={12}>
            <Grid container justifyContent="center" spacing={2}>
              {[0, 1, 2].map((value) => (
                <Grid key={value} item>
                  {/* {value === 0 ? <div>0</div> : null}
                  {value === 1 ? <div>1</div> : null}
                  {value === 2 ? <div>2</div> : null} */}
                </Grid>
              ))}
            </Grid>
          </Grid>
        </>
      )}
    </div>
  );
};
export default Project;
